targets.expr:
(
  sum by (pod) (
    irate(ds_connection_handlers_ldap_requests_seconds_total{namespace=~"$namespace",service=~"$service",pod=~"$pod"}[1m])
  ) +
  sum by (pod) (
    irate(ds_connection_handlers_http_requests_seconds_total{namespace=~"$namespace",service=~"$service",pod=~"$pod"}[1m])
  )
) / (
  sum by (pod) (
    irate(ds_connection_handlers_ldap_requests_count{namespace=~"$namespace",service=~"$service",pod=~"$pod"}[1m])
  ) +
  sum by (pod) (
    irate(ds_connection_handlers_http_requests_count{namespace=~"$namespace",service=~"$service",pod=~"$pod"}[1m])
  )
)

(
  sum by (pod) (
    irate(ds_connection_handlers_ldap_requests_failure_seconds_total{namespace=~"$namespace",service=~"$service",pod=~"$pod"}[1m])
  ) +
  sum by (pod) (
    irate(ds_connection_handlers_http_requests_failure_seconds_total{namespace=~"$namespace",service=~"$service",pod=~"$pod"}[1m])
  )
) / (
  sum by (pod) (
    irate(ds_connection_handlers_ldap_requests_failure_count{namespace=~"$namespace",service=~"$service",pod=~"$pod"}[1m])
  ) +
  sum by (pod) (
    irate(ds_connection_handlers_http_requests_failure_count{namespace=~"$namespace",service=~"$service",pod=~"$pod"}[1m])
  )
)

sum by (pod) (
