targets.expr:
(
  sum by (pod) (
    irate(ds_connection_handlers_ldap_requests_seconds_total{service=~"$service",pod=~"$pod"}[1m])
  ) +
  sum by (pod) (
    irate(ds_connection_handlers_http_requests_seconds_total{service=~"$service",pod=~"$pod"}[1m])
  )
) / (
  sum by (pod) (
    irate(ds_connection_handlers_ldap_requests_count{service=~"$service",pod=~"$pod"}[1m])
  ) +
  sum by (pod) (
    irate(ds_connection_handlers_http_requests_count{service=~"$service",pod=~"$pod"}[1m])
  )
)

(
  sum by (pod) (
    irate(ds_connection_handlers_ldap_requests_failure_seconds_total{service=~"$service",pod=~"$pod"}[1m])
  ) +
  sum by (pod) (
    irate(ds_connection_handlers_http_requests_failure_seconds_total{service=~"$service",pod=~"$pod"}[1m])
  )
) / (
  sum by (pod) (
    irate(ds_connection_handlers_ldap_requests_failure_count{service=~"$service",pod=~"$pod"}[1m])
  ) +
  sum by (pod) (
    irate(ds_connection_handlers_http_requests_failure_count{service=~"$service",pod=~"$pod"}[1m])
  )
)

sum by (pod) (
  irate(ds_connection_handlers_ldap_requests_count{service=~"$service",pod=~"$pod"}[1m])
) or
sum by (pod) (
  irate(ds_connection_handlers_http_requests_count{service=~"$service",pod=~"$pod"}[1m])
) or
sum by (pod) (
  irate(ds_connection_handlers_ldap_requests_failure_count{service=~"$service",pod=~"$pod"}[1m])
) or
sum by (pod) (
  irate(ds_connection_handlers_http_requests_failure_count{service=~"$service",pod=~"$pod"}[1m])
)

sum by (sevice,type) (
  irate(ds_connection_handlers_ldap_requests_count{service=~"$service",pod=~"$pod"}[1m])
) or
sum by (service,type) (
  irate(ds_connection_handlers_http_requests_count{service=~"$service",pod=~"$pod"}[1m])
) or
sum by (service,type) (
  irate(ds_connection_handlers_ldap_requests_failure_count{service=~"$service",pod=~"$pod"}[1m])
) or
sum by (service,type) (
  irate(ds_connection_handlers_http_requests_failure_count{service=~"$service",pod=~"$pod"}[1m])
)

sum by (cluster) (
  (
    sum by (cluster,container_id) (
      irate(ds_connection_handlers_ldap_requests_failure_count{cluster="$kubernetesCluster"}[$__interval])
    ) or
    sum by (cluster,container_id) (
      irate(ds_connection_handlers_http_requests_failure_count{cluster="$kubernetesCluster"}[$__interval])
    )
  )
  * on (container_id) group_left(pod) kube_pod_container_info{cluster="$kubernetesCluster"}
  * on (pod) group_left kube_pod_owner{cluster="$kubernetesCluster",owner_kind="StatefulSet",owner_name=~"$statefulSet"}
)
